css:
body {
  font-family: 'Arial', sans-serif;
  line-height: 1.6;
  color: #333;
  margin: 0;
  padding: 0;
}

header {
  background-color: #3498db;
  color: white;
  text-align: center;
  padding: 2rem;
}

#hero {
  background-color: #2c3e50;
  color: white;
  text-align: center;
  padding: 4rem 2rem;
}

.cta-button {
  display: inline-block;
  background-color: #e74c3c;
  color: white;
  padding: 1rem 2rem;
  text-decoration: none;
  border-radius: 5px;
  font-weight: bold;
  margin-top: 1rem;
}

#features, #testimonials {
  padding: 2rem;
  max-width: 800px;
  margin: 0 auto;
}

blockquote {
  font-style: italic;
  border-left: 3px solid #3498db;
  padding-left: 1rem;
  margin-left: 0;
}

footer {
  background-color: #34495e;
  color: white;
  text-align: center;
  padding: 1rem;
  position: absolute;
  bottom: 0;
  width: 100%;
}

js:
document.querySelector('.cta-button').addEventListener('click', function(e) {
  e.preventDefault();
  alert('Thanks for your interest! (This is a demo)');
});
