css:
body {
  font-family: 'Helvetica', sans-serif;
  line-height: 1.6;
  color: #333;
  max-width: 1000px;
  margin: 0 auto;
  padding: 20px;
}

header {
  background-color: #2c3e50;
  color: #ecf0f1;
  padding: 1rem;
  text-align: center;
}

nav ul {
  list-style-type: none;
  padding: 0;
}

nav ul li {
  display: inline;
  margin-right: 10px;
}

nav a {
  color: #ecf0f1;
  text-decoration: none;
}

section {
  margin-bottom: 2rem;
}

.project {
  background-color: #f9f9f9;
  border: 1px solid #ddd;
  padding: 1rem;
  margin-bottom: 1rem;
}

form {
  display: flex;
  flex-direction: column;
}

form input, form textarea {
  margin-bottom: 1rem;
  padding: 0.5rem;
}

button {
  background-color: #2c3e50;
  color: #ecf0f1;
  border: none;
  padding: 0.5rem 1rem;
  cursor: pointer;
}

footer {
  text-align: center;
  margin-top: 2rem;
  font-size: 0.8rem;
}

js:
document.querySelector('form').addEventListener('submit', function(e) {
  e.preventDefault();
  alert('Form submitted! (This is a demo)');
});
